import React, { useState } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import "./Loom_Loading_Chart.css";
function GreyReport() {
  const [form, setForm] = useState({
    date: "",
    shift: "",
    loom_no: "",
    worker: "",
    meters: "",
    points: "",
    order_no: ""

  });

  function handleSubmit(e) {

    e.preventDefault();

    axios.post("http://localhost:5000/grey_report", form)
      .then(res => {
        console.log(res);
        alert("successful insert");
      })
      .catch(err => {
        console.log(err);
      });

    setForm({
      date: "",
      shift: "",
      loom_no: "",
      worker: "",
      meters: "",
      points: "",
      order_no: ""

    });


  }

  function handleChange(event) {

    const { name, value } = event.target;

    setForm((prv) => {
      return {
        ...prv,
        [name]: value
      }
    });
  }

  return (
    <form onSubmit={handleSubmit}>
      <div class="login">
        <div class="form">
          <NavLink to="/">
            <button>Back</button>
          </NavLink>
          <h2>GreyReport Form</h2>

          <input type="date" value={form.date} onChange={handleChange} name="date" placeholder="Date" required />
          <input type="number" value={form.shift} onChange={handleChange} name="shift" placeholder="Shift" required />
          <input type="number" value={form.loom_no} onChange={handleChange} name="loom_no" placeholder="Loom" required />
          <input type="text" value={form.worker} onChange={handleChange} name="worker" placeholder="Worker" required />
          <input type="number" value={form.meters} onChange={handleChange} name="meters" placeholder="Meters" required />
          <input type="number" value={form.points} onChange={handleChange} name="points" placeholder="points" required />
          <input type="number" value={form.order_no} onChange={handleChange} name="order_no" placeholder="Order Number" required />
          <input type="submit" value="SUBMIT" class="submit" />
        </div>
      </div>
    </form>
  );
}
export default GreyReport;
